                urakan_alkuvuosi,
                urakan_loppuvuosi - 1);

        perform luo_kaikille_tehtaville_testitarjousmaarat ('Pellon MHU testiurakka (3. hoitovuosi)', 1100);

end 
$$ language plpgsql;

